package Test;

public class KYU {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 5;
 		int b = a++;
 		
 		System.out.println(a);
 		System.out.println(b);
 		System.out.println(++a);
 		//System.out.println(b++); // b의 값을 먼저 대입
 		System.out.println(++b);
 		System.out.println(a++ + --b);
	}

}
